mapper.select:
SELECT *
FROM pig_list

    username = #{username}

        AND type = #{type}


        AND time > #{stime}


        AND #{etime} > time


order by id
desc

SELECT p.type, t.typename, SUM(p.money) as total FROM ( SELECT * FROM pig_list
username = #{username}
AND time > #{stime}
AND #{etime} > time
) p LEFT JOIN type_list t ON p.type = t.id GROUP BY p.type ORDER BY p.type
